Script.script:
function Releech_Reaver_Spells() --Create Database if not exists, otherwise update table schema create_tfe_database()
--Load Database to local variable local mydb = db:get_database("skills")
local mysql = "select * from reaverfadedspells where character = '" .. gmcp.Char.Score.name .. "' and alwaysonline = 'yes' and leeched = 'no'" local results = db:fetch_sql(mydb.reaverfadedspells, mysql) --display(results) if next(results) then for _, row in ipairs(results) do send("queue perform "..row.spell) end end end
function Reset_Reaver_Spells() --Create Database if not exists, otherwise update table schema create_tfe_database()
--Load Database to local variable local mydb = db:get_database("skills")
--Set all leeched spells to yes faded db:set(mydb.reaverfadedspells.leeched, "no", db:eq(mydb.reaverfadedspells.character, gmcp.Char.Score.name)) send("queue affect -a") send("gstatus Reaverleech spell list has been reset.") end
