Script.script:
function Releech_Druid_Spells() --Create Database if not exists, otherwise update table schema create_tfe_database()
--Load Database to local variable local mydb = db:get_database("skills")
local mysql = "select * from druidfadedspells where character = '" .. gmcp.Char.Score.name .. "' and alwaysonline = 'yes' and leeched = 'no'" local results = db:fetch_sql(mydb.druidfadedspells, mysql) --display(results) if next(results) then for _, row in ipairs(results) do send("queue cast "..row.spell) end end end
function Reset_Druid_Spells() --Create Database if not exists, otherwise update table schema create_tfe_database()
--Load Database to local variable local mydb = db:get_database("skills")
--Set all leeched spells to yes faded db:set(mydb.druidfadedspells.leeched, "no", db:eq(mydb.druidfadedspells.character, gmcp.Char.Score.name)) fireflies = false lifesight = false hardiness = false arcanesight = false quickening = false puissance = false darklight = false landbinding = false embraceofearth=false embraceofshades=false flamelight = false farsight = false shadowsight = false barrier = false thornshield = false cloyinglight = false clearsight = false send("queue affect -a") send("gstatus Druidleech spell list has been reset.") end
